import React, {Component} from 'react';


class App extends Component{

  constructor(props){
    super(props);
    this.state = {
      cartList: []
    }
  }

  handleCart(){
    this.history.pushState(null, '/Cart');
  }

  handleItem(){
    this.history.pushState(null, '/AddItem');
  }

  componentDidMount() {
     fetch("localhost:5555/list").then(data => data.json()).then(data =>this.setState({cartList: data})).catch(console.log);
  }
  render() {
    return (

      <div>
      <div className = "container">
        <div className = "col-xs-12">
          <h1>Welcome to Shop-R-US</h1>
          <button onClick={this.handleCart} type="button">
          View Cart
          </button> 
          <button onClick={this.handleItem} type="button">
          Add Item
          </button> 

        </div>
      </div>
      </div>

    );
  }

}

export default App;
