import React, {Component} from 'react';
import Cart from './Cart.js';
import AddItem from './AddItem.js';
import { BrowserRouter as Router, Route, Link} from 'react-router-dom'


class App extends Component{

  constructor(props){
    super(props);
    this.state = {
      cartList: []
    }
  }

  handleCart(){
    this.history.pushState(null, 'Cart');
  }

  handleItem(){
    this.history.pushState(null, 'AddItem');
  }

  componentDidMount() {
     fetch("http://jsonplaceholder.typicode.com/todos").then(data => data.json()).then(data =>this.setState({cartList: data})).catch(console.log);
  }
  render() {
    return (
      <Router>

      <div>
      <div className = "container">
        <div className = "col-xs-12">
          <h1>Welcome to Shop-R-US</h1>
          <button onClick={this.handleCart} type="button">
          View Cart
          </button> 
          <button onClick={this.handleItem} type="button">
          Add Item
          </button> 
          <Cart cartList = {this.state.cartList}/>
          <AddItem item/>

        </div>
      </div>
      </div>
      </Router>
    );
  }

}

export default App;
