import React, {Component} from 'react';
import Cart from "./Cart.js"


class App extends Component{

  constructor(props) {
    super(props);
    this.state = {
      cartList: []
    };
  }

  componentDidMount() {
     fetch("localhost:5555/list").then(data => data.json()).then(data =>
       this.setState({
        cartList: data
       }));
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>Welcome to Shop-R-Us!</h1>


        </header>
      </div>
    );
  }

}

export default App;
